import React from "react";
import { useStateValue } from "../state";
import { Entry, Diagnosis } from "../types";

const HospitalEntryComponent = ( { entry } : { entry: Entry }) => {
  const [{ diagnoses }, ] = useStateValue();

  return (
    <div key={entry.id} style={{ "border": "solid 1px black", "margin": "0.5rem 0", "borderRadius": "1rem" }}>
      <div style={{ "padding": "1rem" }}>
      <strong>{entry.date} üè•</strong>
        <p>{entry.description}</p>
          <ul>
            {entry.diagnosisCodes?.map((code: Diagnosis['code']) => {
                return (<li key={code}>{code}: {diagnoses[code]?.name}</li>);
            })}
          </ul>
      </div>
    </div>
  );
};

export default HospitalEntryComponent;
