import React from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Container, Button } from "semantic-ui-react";
import EntryDetails from "../EntryDetails";

import { Entry, Patient } from "../types";
import { apiBaseUrl } from "../constants";
import HealthRatingBar from "../components/HealthRatingBar";
import { useStateValue } from "../state";
import { setSinglePatient } from "../state";
import AddPatientModal from "../AddPatientModal";

const PatientViewPage = () => {
  const [{ patients }, dispatch] = useStateValue();
  const { id } = useParams<{ id: string }>();

  const [modalOpen, setModalOpen] = React.useState<boolean>(false);
  const [error, setError] = React.useState<string | undefined>();
  const openModal = (): void => setModalOpen(true);
  const closeModal = (): void => {
    setModalOpen(false);
    setError(undefined);
  };
  // const submitNewPatient = async (values: PatientFormValues) => {
  //   try {
  //     const { data: newPatient } = await axios.post<Patient>(
  //       `${apiBaseUrl}/patients`,
  //       values
  //     );
  //     dispatch(createPatient(newPatient));
  //     closeModal();
  //     // eslint-disable-next-line @typescript-eslint/no-explicit-any
  //   } catch (e: any) {
  //     console.error(e.response?.data || 'Unknown Error');
  //     setError(e.response?.data?.error || 'Unknown error');
  //   }
  // };

  React.useEffect(() => {
    if (patients[id] && patients[id].ssn) return;
    const fetchPatientList = async () => {
      try {
        const { data: singlePatientData } = await axios.get<Patient>(
          `${apiBaseUrl}/patients/${id}`
        );
        dispatch(setSinglePatient(singlePatientData));
      } catch (e) {
        console.error(e);
      }
    };
    void fetchPatientList();
  }, [dispatch]);

  return (
    <div className="App">
      <Container textAlign="left">
        <h1>Patient</h1>
        <p></p>
      </Container>
      <Container textAlign="left">
        <HealthRatingBar showText={false} rating={1} />  
        <br />
      </Container>
      <Container textAlign="left">
        <h2>{patients[id]?.name}</h2>
      </Container>
      <Container textAlign="left">
        <br />
        {/* remember to remove this button later */}
        <Button onClick={() => {console.log(patients[id]);}}>Data Logged In Console</Button>

        <AddPatientModal
        modalOpen={modalOpen}
        onSubmit={console.log('submitted')}
        error={error}
        onClose={closeModal}
      />
      <Button onClick={() => openModal()}>Add New Patient</Button>

        <p>ssh: {patients[id]?.ssn || 'no ssn'}</p>
        <p>occupation: {patients[id]?.occupation}</p>
      </Container>
      <br />
      <Container textAlign="left">
        <h3>Entries</h3>

        {patients[id]?.entries?.map((entry: Entry) => {
          return <EntryDetails key={entry.id} entry={entry} />;
        })}

      </Container>
    </div>
  );
};

export default PatientViewPage;
